import * as WebBrowser from 'expo-web-browser';
import React, { useEffect, useState } from 'react';
import {
	Image,
	Platform,
	StyleSheet,
	Text,
	TouchableOpacity,
	View,
	FlatList,
} from 'react-native';
import { ScrollView } from 'react-native-gesture-handler';

import { MonoText } from '../components/StyledText';
import { getCharacter } from '../hooks/useApi';

export default function HomeScreen() {
	const [characters, setCharacters] = useState([]);
	const [currentPage, setCurrentPage] = useState(4);
	const [pageLimit, setPageLimit] = useState(2);

	useEffect(() => {
		async function getTest() {
			console.log(currentPage);
			const response = await getCharacter({ page: currentPage });
			setCharacters((prevValues) => [...prevValues, ...response.results]);
			setPageLimit(response.info.pages);
		}
		getTest();
	}, [currentPage]);

	return (
		<View style={styles.container}>
			<FlatList
				data={characters}
				renderItem={({ item }) => <p>{item.name}</p>}
				keyExtractor={(char) => `${char.id} - ${char.name}`}
				onEndReached={() => {
					if (pageLimit > currentPage) {
						console.log('times');
						setCurrentPage((prev) => prev + 1);
					}
				}}
				onEndReachedThreshold={0.4}
			/>
			{/* <View style={styles.getStartedContainer}>
					{characters.length &&
						characters.map((char) => {
							return <p>{char.name}</p>;
						})}
				</View> */}
		</View>
	);
}

HomeScreen.navigationOptions = {
	header: null,
};

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: '#fff',
	},
	developmentModeText: {
		marginBottom: 20,
		color: 'rgba(0,0,0,0.4)',
		fontSize: 14,
		lineHeight: 19,
		textAlign: 'center',
	},
	contentContainer: {
		paddingTop: 30,
	},
	welcomeContainer: {
		alignItems: 'center',
		marginTop: 10,
		marginBottom: 20,
	},
	welcomeImage: {
		width: 100,
		height: 80,
		resizeMode: 'contain',
		marginTop: 3,
		marginLeft: -10,
	},
	getStartedContainer: {
		alignItems: 'center',
		marginHorizontal: 50,
	},
	homeScreenFilename: {
		marginVertical: 7,
	},
	codeHighlightText: {
		color: 'rgba(96,100,109, 0.8)',
	},
	codeHighlightContainer: {
		backgroundColor: 'rgba(0,0,0,0.05)',
		borderRadius: 3,
		paddingHorizontal: 4,
	},
	getStartedText: {
		fontSize: 17,
		color: 'rgba(96,100,109, 1)',
		lineHeight: 24,
		textAlign: 'center',
	},
	tabBarInfoContainer: {
		position: 'absolute',
		bottom: 0,
		left: 0,
		right: 0,
		...Platform.select({
			ios: {
				shadowColor: 'black',
				shadowOffset: { width: 0, height: -3 },
				shadowOpacity: 0.1,
				shadowRadius: 3,
			},
			android: {
				elevation: 20,
			},
		}),
		alignItems: 'center',
		backgroundColor: '#fbfbfb',
		paddingVertical: 20,
	},
	tabBarInfoText: {
		fontSize: 17,
		color: 'rgba(96,100,109, 1)',
		textAlign: 'center',
	},
	navigationFilename: {
		marginTop: 5,
	},
	helpContainer: {
		marginTop: 15,
		alignItems: 'center',
	},
	helpLink: {
		paddingVertical: 15,
	},
	helpLinkText: {
		fontSize: 14,
		color: '#2e78b7',
	},
});
